import { useState } from 'react'

export default function App() {
  const [selectedFirstCur, setSelectedFirstCur] = useState('')
  const [selectedSecondCur, setSelectedSecondCur] = useState('')
  const [amount, setAmount] = useState('')
  console.log(selectedSecondCur)
  return (
    <div>
      <input
        type='text'
        value={amount}
        onChange={(e) => setAmount(e.target.value)}
      />
      <select
        value={selectedFirstCur}
        onChange={(e) => setSelectedFirstCur(e.target.value)}
      >
        <option value='USD'>USD</option>
        <option value='EUR'>EUR</option>
        <option value='CAD'>CAD</option>
        <option value='INR'>INR</option>
      </select>
      <select
        value={selectedFirstCur}
        onChange={(e) => setSelectedSecondCur(e.target.value)}
      >
        <option value='USD'>USD</option>
        <option value='EUR'>EUR</option>
        <option value='CAD'>CAD</option>
        <option value='INR'>INR</option>
      </select>
      <p>OUTPUT</p>
    </div>
  )
}
